mapper.select:
select blog.*, category.name as categoryName, user.name as userName from blog left join category on blog.category_id = category.id left join user on blog.user_id = user.id
and blog.title like concat('%', #{title}, '%') and category.name like concat('%', #{categoryName}, '%') and user.name like concat('%', #{userName}, '%') and user.id = #{userId}
order by id desc
